document.getElementById("runCode").addEventListener('click', async () => {
    let [tab] = await chrome.tabs.query({ active: true, currentWindow: true });

    if (tab.url.includes("teamblind.com")) {
        chrome.scripting.executeScript({
            target: { tabId: tab.id },
            function: async () => {
                let sleep = (delay) => new Promise((resolve) => setTimeout(resolve, delay));

                let findViewMoreBtn = () => {
                    let buttons = [...document.getElementsByClassName("btn_more")].filter((btn) => btn.innerText.includes("+ View more comments..."));
                    if (buttons.length != 1) {
                        return null
                    }
                    return buttons[0]
                }

                console.log("Expanding comments...");
                let times = 0;
                while (true) {
                    let btn = findViewMoreBtn();
                    if (btn == null) {
                        break;
                    }
                    btn.click();
                    times += 1;
                    await sleep(2500);
                }
                console.log(`Finished expanding comments ${times} time(s)!`);
            },
        });
    } else {
        chrome.scripting.executeScript({
            target: { tabId: tab.id },
            function: async () => {
                console.log("This code is only active on teamblind.com.");
            },
        });
    }
});
